import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import SearchBar from '../SearchBar/SearchBar';
import './Navbar.scss';
import menuIcon from '../../assets/bars.png';
import { supabase } from '../../utils/supabase'; // Import supabase client

function Navbar() {
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState(null); // State to store current user

  useEffect(() => {
    const fetchCurrentUser = async () => {
      const user = supabase.auth.user(); // Get current user
      setCurrentUser(user);
    };

    fetchCurrentUser();
  }, []);

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleMenuClick = () => {
    setIsMenuOpen(false);
  };

  const handleSignOut = async () => {
    await supabase.auth.signOut(); // Sign out the user
  };

  return (
    <nav className='navbar'>
      <div className='menu-and-logo-container'>
        <div className="menu-toggle" onClick={handleMenuToggle}>
          <img src={menuIcon} alt="Menu" />
        </div>
        <h2 className='logo'>
          <Link to="/" className='navbar__link'>Cinema Chef</Link>
        </h2>
      </div>
      {isMenuOpen && <div className="overlay" onClick={handleMenuToggle} />}
      {isMenuOpen && (
        <ul className={`dropdown-menu ${isMenuOpen ? 'open' : ''}`}>
          {/* Your menu items here */}
        </ul>
      )}
      <ul className='navbar__list'>
        {/* Your navbar items here */}
      </ul>
      <div className='navbar--right'>
        <SearchBar />
        {currentUser ? (
          <div className='navbar__login'>
            Welcome {currentUser.email.split('@')[0]} {/* Display username */}
            <button onClick={handleSignOut} className='navbar__link'>Sign out</button> {/* Sign out button */}
          </div>
        ) : (
          <div className='navbar__login'>
            <Link to="/login" className='navbar__link'>Login</Link>
          </div>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
