import React, { useState } from 'react';
import axios from 'axios';

const AuthComponent = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const [isRegister, setIsRegister] = useState(false);

  const handleAuth = async () => {
    try {
      const endpoint = isRegister ? 'http://localhost:5000/register' : 'http://localhost:5000/login';
      const response = await axios.post(endpoint, formData);
      console.log(response.data); // Handle the response accordingly
    } catch (error) {
      console.error(`Error during ${isRegister ? 'registration' : 'login'}:`, error);
    }
  };

  return (
    <div>
      <h2>{isRegister ? 'Register' : 'Login'}</h2>
      <label>
        Username:
        <input
          type="text"
          value={formData.username}
          onChange={(e) => setFormData({ ...formData, username: e.target.value })}
        />
      </label>
      <br />
      <label>
        Password:
        <input
          type="password"
          value={formData.password}
          onChange={(e) => setFormData({ ...formData, password: e.target.value })}
        />
      </label>
      <br />
      <button onClick={handleAuth}>{isRegister ? 'Register' : 'Login'}</button>
      <br />
      <p onClick={() => setIsRegister(!isRegister)}>
        {isRegister ? 'Already have an account? Login here' : 'Need an account? Register here'}
      </p>
    </div>
  );
};

export default AuthComponent;
