import React, { useState } from 'react';

const SearchBar = ({ onMovieNameSearch, onRecipeNameSearch }) => {
  const [query, setQuery] = useState('');

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  const handleMovieNameSearch = (e) => {
    e.preventDefault();
    onMovieNameSearch(query);
  };

  const handleRecipeNameSearch = (e) => {
    e.preventDefault();
    onRecipeNameSearch(query);
  };

  return (
    <div>
      <form onSubmit={handleMovieNameSearch}>
        <input type="text" value={query} onChange={handleChange} placeholder="Search by movie name" />
        <button type="submit">Search Movies</button>
      </form>
      <form onSubmit={handleRecipeNameSearch}>
        <input type="text" value={query} onChange={handleChange} placeholder="Search by recipe name" />
        <button type="submit">Search Recipes</button>
      </form>
    </div>
  );
};

export default SearchBar;
