import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import SearchTag from '../SearchTag/SearchTag';
import './Home.scss';

function Home() {
  const [likedRecipes, setLikedRecipes] = useState([]);
  const [renderedRecipeIds, setRenderedRecipeIds] = useState([]);
  const navigate = useNavigate();

  const mealTypes = ['breakfast', 'lunch', 'dinner', 'dessert'];
  const dietaryRestrictions = ['vegan', 'vegetarian', 'gluten_free', 'none'];
  const movieGenres = [
    'action',
    'comedy',
    'drama',
    'thriller',
    'horror',
    'sci-fi',
    'fantasy',
    'romance',
    'animated',
    'documentary',
  ];

  useEffect(() => {
    axios
      .get('http://localhost:5000/top-liked-recipes')
      .then((response) => {
        setLikedRecipes(response.data);
      })
      .catch((error) => {
        console.error('Error fetching top liked recipes:', error);
      });
  }, []);

  const handleSearch = async (tag, searchType) => {
    try {
      const modifiedTag = searchType === 'movie-genre' && tag === 'sci-fi' ? 'sci_fi' : tag;
      const response = await axios.get(`http://localhost:5000/recipes/${searchType}/${modifiedTag}`);
      navigate(`/search/${searchType}/${modifiedTag}`);
    } catch (error) {
      console.error(`Error fetching recipes with ${searchType}:`, error);
    }
  };

  // function truncate(text) {
  //   const max = 110;
  //   if (text.length > max) {
  //     return text.substring(0, max) + '...';
  //   }
  //   return text;
  // }

  const renderRecipe = (recipe) => {
    if (!renderedRecipeIds.includes(recipe.recipe_id)) {
      setRenderedRecipeIds((prevIds) => [...prevIds, recipe.recipe_id]);
      return (
        <div key={recipe.recipe_id}>
          <Link to={`/recipes/${recipe.recipe_id}`}>
            {recipe.image && <img className="image" src={recipe.image} alt={recipe.title} />}
            <h3>{recipe.title}</h3>
            <p>{truncate(recipe.description)}</p>
          </Link>
        </div>
      );
    }
    return null; // Recipe already rendered
  };

  const headlineRecipe = likedRecipes.length > 0 && likedRecipes[0];
  const highlightedRecipes = likedRecipes.slice(1, 4);

  const otherLikedRecipes = likedRecipes.slice(4).filter((recipe) => !renderedRecipeIds.includes(recipe.recipe_id));

  return (
    <div className="home">
      <h1>Explore culinary recipes based on cinematic masterpieces</h1>

      <div className="showcase-section">
        <div className="headline-section">
          <h2>Our Favorite Recipe</h2>
          {headlineRecipe && (
            <div className="headline-recipe">
              <Link to={`/recipes/${headlineRecipe.recipe_id}`}>
                <h3>{headlineRecipe.title}</h3>
                {headlineRecipe.image && <img className="image" src={headlineRecipe.image} alt={headlineRecipe.title} />}
                <p>{truncate(headlineRecipe.description)}</p>
              </Link>
            </div>
          )}
        </div>
        <div className="highlighted-section">
          <h2>Hot Recipes</h2>
          {highlightedRecipes.map((recipe) => (
            <div key={recipe.recipe_id} className="highlighted-recipe">
              <Link to={`/recipes/${recipe.recipe_id}`}>
                {recipe.image && <img className="image" src={recipe.image} alt={recipe.title} />}
                <h3>{recipe.title}</h3>
              </Link>
            </div>
          ))}
        </div>
      </div>

      <div className="search-section">
        {/* Search section */}
      </div>

      <div className="other-liked-section">
        <h2>Enjoy more recipes</h2>
        <div className="other-liked-recipes">
          {otherLikedRecipes.map(renderRecipe)}
        </div>
      </div>
    </div>
  );
}

export default Home;
