import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import SearchTag from '../SearchTag/SearchTag';

function Home() {
  const [likedRecipes, setLikedRecipes] = useState([]);

  const mealTypes = ['breakfast', 'lunch', 'dinner', 'dessert'];
  const dietaryRestrictions = ['vegan', 'vegetarian', 'gluten_free', 'none'];
  const movieGenres = ['action', 'comedy', 'drama', 'thriller', 'horror', 'sci-fi', 'fantasy', 'romance', 'animated', 'documentary'];

  useEffect(() => {
    axios.get('http://localhost:5000/top-liked-recipes')
      .then((response) => {
        console.log('Response data:', response.data);
        setLikedRecipes(response.data);
      })
      .catch((error) => {
        console.error('Error fetching top liked recipes:', error);
      });
  }, []);

  const handleSearch = (tag) => {
    console.log('Searching for recipes with tag:', tag);
    // Perform a search query based on the selected tag
    // For ex: axios.get(`http://localhost:5000/recipes?tag=${tag}`)
    //   .then((response) => {
    //     console.log('Recipes with tag:', response.data);
    //   })
    //   .catch((error) => {
    //     console.error('Error fetching recipes with tag:', error);
    //   });
  };

  return (
    <div>
      <h1>Cinema Chef</h1>
      <p>Explore culinary recipes based on cinematic masterpieces</p>

      <h4>Search by meal type:</h4>
      <SearchTag tags={mealTypes} onSearch={handleSearch} />
      <h4>Search by dietary restriction:</h4>
      <SearchTag tags={dietaryRestrictions} onSearch={handleSearch} />
      <h4>Search by movie genre:</h4>
      <SearchTag tags={movieGenres} onSearch={handleSearch} />

      <h2>Most Liked Recipes</h2>
      <ul>
        {likedRecipes.map((recipe) => (
          <li key={recipe.recipe_id}>
            <Link to={`/recipes/${recipe.recipe_id}`}>
              <h3>{recipe.title}</h3>
              <p>{recipe.movie_title}</p>
              <p>{recipe.ingredients}</p>
              <p>{recipe.instructions}</p>
              <p>Likes: {recipe.likes}</p>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
