import React, { useState, useEffect } from 'react';
import axios from 'axios';

const YourRecipes = () => {
  const [userRecipes, setUserRecipes] = useState([]);
  const authToken = localStorage.getItem('authToken');

  useEffect(() => {
    const fetchUserRecipes = async () => {
      try {
        console.log('Fetching user recipes...');

        const response = await axios.get('http://localhost:5000/user-recipes', {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        });

        console.log('Axios Request Config:', response.config);
        console.log('User Recipes Response:', response.data);
        setUserRecipes(response.data);
      } catch (error) {
        console.error('Error fetching user recipes:', error);
      }
    };

    if (authToken) {
      fetchUserRecipes();
    }
  }, [authToken]);

  console.log('User Recipes:', userRecipes);

  return (
    <div>
      <h2>Your Recipes</h2>
      {authToken ? (
        <ul>
          {userRecipes.map((recipe) => (
            <li key={recipe.recipe_id}>
              <h3>{recipe.title}</h3>
              <p>{recipe.movie_title}</p>
              {recipe.image && <img src={recipe.image} alt={recipe.title} />}
            </li>
          ))}
        </ul>
      ) : (
        <p>Please log in to view your recipes.</p>
      )}
    </div>
  );
};

export default YourRecipes;
