import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import DeleteRecipe from '../DeleteRecipe/DeleteRecipe';
import UpdateRecipe from '../UpdateRecipe/UpdateRecipe';

function Recipe() {
  const [recipe, setRecipe] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [editableField, setEditableField] = useState(null);
  const navigate = useNavigate();

  const { id } = useParams();

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/recipes/${id}`);
        const { data } = response;
        setRecipe(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching recipe:', error);
        setIsLoading(false);
      }
    };

    fetchRecipe();
  }, [id]);

  const handleDelete = async () => {
    try {
      const response = await axios.delete(`http://localhost:5000/recipes/${id}`);

      if (response.data.deletedRecipe) {
        console.log('Deleted Recipe:', response.data.deletedRecipe);
        navigate('/');
      } else {
        console.error('Failed to delete recipe:', response.data);
      }
    } catch (error) {
      if (error.response && error.response.status === 404) {
        navigate('/');
      } else {
        console.error('Error deleting recipe:', error);
      }
    }
  };

  return (
    <div className="recipe">
      {isLoading && <div>Loading...</div>}

      {!isLoading && !recipe && <div>Recipe not found</div>}

      {!isLoading && recipe && (
        <>
          <h2 className="recipe_title">
            {editableField === 'title' ? (
              <UpdateRecipe field="title" initialValue={recipe.title} />
            ) : (
              <>
                {recipe.title}{' '}
                <button onClick={() => setEditableField('title')}>Edit</button>
              </>
            )}
          </h2>

          <p className="recipe_movie_title">
            <strong>Movie Title:</strong>{' '}
            {editableField === 'movie_title' ? (
              <UpdateRecipe field="movie_title" initialValue={recipe.movie_title} />
            ) : (
              <>
                {recipe.movie_title}{' '}
                <button onClick={() => setEditableField('movie_title')}>Edit</button>
              </>
            )}
          </p>

          {recipe.image && (
            <img className="recipe_image" src={recipe.image} alt={recipe.title} />
          )}

          <p className="recipe_ingredients">
            <strong>Ingredients:</strong>{' '}
            {editableField === 'ingredients' ? (
              <UpdateRecipe field="ingredients" initialValue={recipe.ingredients} />
            ) : (
              <>
                {recipe.ingredients}{' '}
                <button onClick={() => setEditableField('ingredients')}>Edit</button>
              </>
            )}
          </p>

          <p className="recipe_instructions">
            <strong>Instructions:</strong>{' '}
            {editableField === 'instructions' ? (
              <UpdateRecipe field="instructions" initialValue={recipe.instructions} />
            ) : (
              <>
                {recipe.instructions}{' '}
                <button onClick={() => setEditableField('instructions')}>Edit</button>
              </>
            )}
          </p>

          <DeleteRecipe recipeId={id} onDelete={handleDelete} />
        </>
      )}
    </div>
  );
}

export default Recipe;
