import React, { useState } from 'react';
import axios from 'axios';
import './Auth.scss';

const AuthComponent = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [user, setUser] = useState(null);
  const [isRegisterVisible, setIsRegisterVisible] = useState(false);
  const [isLoginFormVisible, setIsLoginFormVisible] = useState(true);

  const handleAuth = async () => {
    try {
      const endpoint = isRegisterVisible
        ? 'http://localhost:5000/auth/register'
        : 'http://localhost:5000/auth/login';

      const response = await axios.post(endpoint, formData);
      setUser(response.data.user);

      localStorage.setItem('authToken', response.data.token);
      setFormData({ username: '', password: '' });
      window.location.reload();
    } catch (error) {
      console.error(`Error during ${isRegisterVisible ? 'registration' : 'login'}:`, error);
    }
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('authToken');
    window.location.reload();
  };

  const switchFormVisibility = () => {
    setIsLoginFormVisible(!isLoginFormVisible);
    setIsRegisterVisible(!isRegisterVisible);
  };

  return (
    <div className="auth-container">
      {user ? (
        <div>
          <p>Welcome, {user.username}!</p>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <div className="auth-content">
          {isLoginFormVisible && (
            <div className="auth-form">
              <label>
                Username:
                <input
                  type="text"
                  value={formData.username}
                  onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                />
              </label>
              <label>
                Password:
                <input
                  type="password"
                  value={formData.password}
                  onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                />
              </label>
              <button onClick={handleAuth}>Login</button>
            </div>
          )}
          {!isLoginFormVisible && (
            <div className="auth-form">
              <label>
                Username:
                <input
                  type="text"
                  value={formData.username}
                  onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                />
              </label>
              <label>
                Password:
                <input
                  type="password"
                  value={formData.password}
                  onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                />
              </label>
              <button onClick={handleAuth}>Register</button>
            </div>
          )}
          <div onClick={switchFormVisibility} className="auth-switch">
            {isLoginFormVisible ? 'Need to register?' : 'Login instead'}
          </div>
        </div>
      )}
    </div>
  );
};

export default AuthComponent;
