import React, { useState, useEffect } from 'react';
import axios from 'axios';

function LikeRecipe({ userId, recipeId, authToken }) {
  const [isLiking, setIsLiking] = useState(false);
  const [liked, setLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(0);

  useEffect(() => {
    const checkLikedRecipe = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/check-liked-recipe?user_id=${userId}&recipe_id=${recipeId}`, {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        });

        setLiked(response.data.liked);
      } catch (error) {
        console.error('Error checking liked recipe:', error);
      }
    };

    checkLikedRecipe();
  }, [userId, recipeId, authToken]);

  const handleLike = async () => {
    if (isLiking) return;

    setIsLiking(true);
    try {
      if (liked) {
        await axios.post(
          'http://localhost:5000/delete-like',
          { user_id: userId, recipe_id: recipeId },
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
              'Content-Type': 'application/json',
            },
          }
        );
        setLiked(false);
        setLikeCount(prevCount => Math.max(0, prevCount - 1));
      } else {
        await axios.post(
          'http://localhost:5000/like-recipe',
          { user_id: userId, recipe_id: recipeId },
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
              'Content-Type': 'application/json',
            },
          }
        );
        setLiked(true);
        setLikeCount(prevCount => prevCount + 1);
      }
    } catch (error) {
      console.error('Error liking recipe:', error);
    } finally {
      setIsLiking(false);
    }
  };

  return (
    <div>
      <button onClick={handleLike} disabled={isLiking}>
        Like
      </button>
      <span>Likes: {likeCount}</span>
    </div>
  );
}

export default LikeRecipe;
