import React, { useState } from 'react';
import './SearchTag.scss'

function SearchTag({ tags, onSearch, searchType }) {
  const [selectedTag, setSelectedTag] = useState('');

  // const handleTagChange = (event) => {
  //   setSelectedTag(event.target.value);
  // };

  // const handleSearch = () => {
  //   onSearch(selectedTag, searchType);
  // };

  const handleTagChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedTag(selectedValue);
    onSearch(selectedValue, searchType);
  };

  return (
    <div>
      {/* <label htmlFor="tags">Select a Tag:</label> */}
      <select id="tags" value={selectedTag} onChange={handleTagChange}>
        <option value="">Select</option>
        {tags.map((tag) => (
          <option onClick={handleSearch} key={tag} value={tag}>{tag}</option>
        ))}
      </select>
      {/* <button className='button--searchtag' onClick={handleSearch}>Search</button> */}
    </div>
  );
}

export default SearchTag;
