import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const SearchBar = () => {
  const [query, setQuery] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      if (!query.trim()) {
        console.log('Please enter a search query');
        return;
      }

      const movieNameResponse = await axios.get(`http://localhost:5000/recipes/movie-name/${query}`);
      const recipeNameResponse = await axios.get(`http://localhost:5000/recipes/recipe-name/${query}`);

      const searchResults = {
        movieName: movieNameResponse.data,
        recipeName: recipeNameResponse.data,
      };

      console.log('Search results:', searchResults);
      navigate(`/search/${searchType}/${query}`);

      // const response = await axios.get(`http://localhost:5000/recipes/${searchType}/${tag}`);
      // console.log(`Recipes with ${searchType} ${tag}:`, response.data);
      // navigate(`/search/${searchType}/${tag}`)
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input type="text" value={query} onChange={handleChange} placeholder="Search for recipes" />
        <button type="submit">Search</button>
      </form>
    </div>
  );
};

export default SearchBar;
