import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UpdateRecipe({ field, initialValue, onUpdate, id }) {
  const [isEditing, setIsEditing] = useState(false);
  const [value, setValue] = useState(initialValue);

  useEffect(() => {
    setValue(initialValue);
  }, [initialValue]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setValue(initialValue);
  };

  const handleChange = (e) => {
    setValue(e.target.value);
  };

  const handleFileChange = (e) => {
    setValue(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    setIsEditing(false);

    try {
      const formData = new FormData();
      formData.append(field, field === 'image' ? value : JSON.stringify(value));

      const response = await axios.put(`http://localhost:5000/recipes/${id}`, formData);
      console.log(`Recipe ${field} updated:`, response.data);

      if (onUpdate) {
        onUpdate(field, value); // Pass the field and updated value to the parent component
      }
    } catch (error) {
      console.error(`Error updating ${field}:`, error);
    }
  };

  return (
    <div>
      {isEditing ? (
        <form onSubmit={handleSubmit}>
          {field === 'image' ? (
            <>
              <label>Choose new image</label>
              <input type="file" onChange={handleFileChange} accept="image/*" />
            </>
          ) : (
            <>
              <label>{field}</label>
              <textarea name={field} value={value} onChange={handleChange} required />
            </>
          )}
          <button type="submit">Update</button>
          <button type="button" onClick={handleCancel}>
            Cancel
          </button>
        </form>
      ) : (
        <button type="button" onClick={handleEdit}>
          Edit {field}
        </button>
      )}
    </div>
  );
}

export default UpdateRecipe;
