import React, { useState } from 'react';
import UpdateRecipe from './UpdateRecipe'

function Recipe({ recipe }) {
  const [isEditing, setIsEditing] = useState(false);

  const handleClick = () => {
    setIsEditing(!isEditing)
  }

  return (
    <div>
      {isEditing ? (
        <UpdateRecipe recipe={recipe} />
      ) : (
        <div className='recipe'>
          <h2 className='recipe_title'>{recipe.title}</h2>
          <p className='recipe_movie_title'><strong>Movie Title:</strong> {recipe.movie_title}</p>
          {recipe.image && <img className='recipe_image' src={recipe.image} alt={recipe.title} />}
          <p className='recipe_ingredients'><strong>Ingredients:</strong> {recipe.ingredients}</p>
          <p className='recipe_instructions'><strong>Instructions:</strong> {recipe.instructions}</p>
          <button className='edit_button' onClick={handleClick}>{isEditing ? 'Cancel' : 'Edit recipe'}</button>
        </div>
      )}
    </div>
  );
}

export default Recipe;
