import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UpdateRecipe({ field, initialValue, onUpdate, id }) {
  const [isEditing, setIsEditing] = useState(false);
  const [value, setValue] = useState(initialValue);

  useEffect(() => {
    setValue(initialValue);
  }, [initialValue]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setValue(initialValue);
  };

  // 2.27
  const handleChange = (e) => {
    setValue(e.target.value.toString());
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setValue(file);
  };

  // const handleSubmit = async (e) => {
  //   e.preventDefault();

  //   setIsEditing(false);

  //   try {
  //     const formData = new FormData();

  //     if (field === 'image') {
  //       formData.append('image', value);
  //     } else {
  //       formData.append('field', field);
  //       formData.append('value', value.toString());
  //     }


  //     const response = await axios.put(`http://localhost:5000/recipes/${id}`, formData);

  //     if (onUpdate) {

  //       onUpdate(field, field === 'image' ? response.data.image : response.data[field]);
  //     }
  //   } catch (error) {
  //     console.error(`Error updating ${field}:`, error);
  //   }
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();

    setIsEditing(false);

    try {
      const formData = new FormData();

      if (field === 'image') {
        // 2.27
        // formData.append('image', value);
        formData.append('field', 'image');
        formData.append('image', value);
      } else {
        formData.append('field', field);
        formData.append('value', value.toString());
      }

      console.log('Field:', field);
      console.log('Value:', value);

      const response = await axios.put(`http://localhost:5000/recipes/${id}`, formData);

      console.log('Response data:', response.data);

      if (onUpdate) {
        onUpdate(field, field === 'image' ? response.data.image : response.data[field]);
      }
    } catch (error) {
      console.error(`Error updating ${field}:`, error);
    }
  };


  return (
    <div>
      {isEditing ? (
        <form onSubmit={handleSubmit}>
          {field === 'image' ? (
            <>
              <label>Choose new image</label>
              <input type="file" onChange={handleFileChange} accept="image/*" />
            </>
          ) : (
            <>
              <label>{field}</label>
              <textarea name={field} value={value} onChange={handleChange} required />
            </>
          )}
          <button type="submit">Update</button>
          <button type="button" onClick={handleCancel}>
            Cancel
          </button>
        </form>
      ) : (
        <button type="button" onClick={handleEdit}>
          Edit {field}
        </button>
      )}
    </div>
  );
}

export default UpdateRecipe;
