import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UpdateRecipe({ field, initialValue, recipe_id, onUpdate }) {
  const [value, setValue] = useState(initialValue);

  useEffect(() => {
    setValue(initialValue);
  }, [initialValue]);

  const handleChange = (e) => {
    setValue(e.target.value);
  };

  const handleFileChange = (e) => {
    setValue(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append(field, value);

    try {
      // *need to update later with hosting storage link
      const response = await axios.put(`http://localhost:5000/recipes/${recipe_id}`, formData);

      console.log(`Recipe ${field} updated:`, response.data);

      if (onUpdate) {
        onUpdate(response.data);
      }
    } catch (error) {
      console.error(`Error updating ${field}:`, error);
    }
  };

  return (
    <div>
      <h2>Update {field}</h2>
      <form onSubmit={handleSubmit}>
        {field === 'image' ? (
          <>
            <label>Choose new image</label>
            <input type="file" onChange={handleFileChange} accept="image/*" />
          </>
        ) : (
          <>
            <label>{field}</label>
            <input type="text" name={field} value={value} onChange={handleChange} required />
          </>
        )}
        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default UpdateRecipe;
