import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import DeleteRecipe from '../DeleteRecipe/DeleteRecipe';
import UpdateRecipe from '../UpdateRecipe/UpdateRecipe';

function Recipe() {
  const [recipe, setRecipe] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  // const [editableField, setEditableField] = useState(null);
  const navigate = useNavigate();

  const { id } = useParams();

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/recipes/${id}`);
        const { data } = response;
        setRecipe(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching recipe:', error);
        setIsLoading(false);
      }
    };

    fetchRecipe();
  }, [id]);

  const handleDelete = async () => {
    try {
      const response = await axios.delete(`http://localhost:5000/recipes/${id}`);

      if (response.data.deletedRecipe) {
        console.log('Deleted Recipe:', response.data.deletedRecipe);
        navigate('/');
      } else {
        console.error('Failed to delete recipe:', response.data);
      }
    } catch (error) {
      if (error.response && error.response.status === 404) {
        navigate('/');
      } else {
        console.error('Error deleting recipe:', error);
      }
    }
  };

  // const handleUpdate = async (field, updatedValue) => {
  //   try {
  //     // Create a copy of the current recipe for optimistic update
  //     const updatedRecipe = { ...recipe };

  //     if (field === 'image') {
  //       updatedRecipe[field] = updatedValue;
  //     } else {
  //       updatedRecipe[field] = updatedValue;
  //     }

  //     setRecipe(updatedRecipe);

  //     console.log(`Recipe ${field} updated (optimistically):`, updatedRecipe);

  //     const formData = new FormData();
  //     formData.append(field, updatedValue);

  //     const response = await axios.put(`http://localhost:5000/recipes/${id}`, formData);

  //     console.log(`Recipe ${field} updated (server response):`, response.data);

  //   } catch (error) {
  //     console.error(`Error updating ${field}:`, error);
  //     // Handle the error, e.g., display an error message

  //     // If an error occurs, you might want to revert the local state
  //     // to its previous state to maintain consistency
  //     // setRecipe(previousRecipe);
  //   }
  // };

  const handleUpdate = async (field, updatedValue) => {
    try {
      const updatedRecipe = { ...recipe };
      updatedRecipe[field] = updatedValue;
      setRecipe(updatedRecipe);

      const formData = new FormData();
      formData.append(field, updatedValue);

      const response = await axios.put(`http://localhost:5000/recipes/${id}`, formData);

      console.log(`Recipe ${field} updated (server response):`, response.data);
    } catch (error) {
      console.error(`Error updating ${field}:`, error);
    }
  };

  return (
    <div className="recipe">
      <h2 className="recipe_title">
        {recipe.title}
        <UpdateRecipe field="title" initialValue={recipe.title} recipe_id={id} onUpdate={handleUpdate} />
      </h2>

      <p className="recipe_movie_title">
        <strong>Movie Title:</strong> {recipe.movie_title}
        <UpdateRecipe
          field="movie_title"
          initialValue={recipe.movie_title}
          recipe_id={id}
          onUpdate={handleUpdate}
        />
      </p>

      {recipe.image && (
        <div>
          <img className="recipe_image" src={recipe.image} alt={recipe.title} />
          <UpdateRecipe field="image" initialValue={recipe.image} recipe_id={id} onUpdate={handleUpdate} />
        </div>
      )}

      <p className="recipe_ingredients">
        <strong>Ingredients:</strong> {recipe.ingredients}
        <UpdateRecipe
          field="ingredients"
          initialValue={recipe.ingredients}
          recipe_id={id}
          onUpdate={handleUpdate}
        />
      </p>

      <p className="recipe_instructions">
        <strong>Instructions:</strong> {recipe.instructions}
        <UpdateRecipe
          field="instructions"
          initialValue={recipe.instructions}
          recipe_id={id}
          onUpdate={handleUpdate}
        />
      </p>

      <DeleteRecipe recipeId={id} onDelete={handleDelete} />
    </div>
  );
}

export default Recipe;
