import React, { useState } from 'react';
import axios from 'axios';

const DeleteRecipe = ({ recipeId, onDelete, authToken }) => {
  const [isDeleting, setIsDeleting] = useState(false);

  const handleDelete = async () => {
    if (isDeleting) return; // Prevent multiple clicks

    setIsDeleting(true); // Disable the button
    console.log('Delete button clicked');
    try {
      if (!authToken) {
        console.error('Authentication token is missing.');
        return;
      }

      const response = await axios.delete(`http://localhost:5000/recipes/${recipeId}`, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });

      console.log('Delete response status:', response.status);

      if (response.status === 200) {
        if (onDelete) {
          onDelete();
        }
      } else {
        console.error('Failed to delete recipe:', response.data);
      }
    } catch (error) {
      console.error('Error deleting recipe:', error);
    } finally {
      setIsDeleting(false); // Re-enable the button
    }
  };

  return (
    <button onClick={handleDelete} disabled={isDeleting}>
      Delete Recipe
    </button>
  );
};

export default DeleteRecipe;
